
function init() { 
	var ww = layer_dimension('tarot_layerframe');
	spreading_offset_x += ww.left;
	spreading_offset_y += ww.top;
	spreading_target_x += ww.left;
	spreading_target_y += ww.top;
	if ( screen.width < 1024 ) {
		spreading_spacer         = 4;
	}
	init_common();
	card[1][3]=300; card[1][4]=50; card[1][7]='Tageskarte';
	if ( screen.width < 1024 ) {
		deck_path   = 'http://karten.tarot-server.net/symbolon/075/';
		deck_ext    = 'gif';
		cards_width = 75;
		card[1][3]=300; card[1][4]=50;
	}
	for (var i = 0; i < card.length; ++i) {
		if ( i > 0 || card[i][3] > 0 || card[i][4] > 0 ) {
			card[i][3] += ww.left;
			card[i][4] += ww.top;
		}
	}
}
       

var titel  = new Array(78);
var ctext  = new Array(78);
